          Create New Employee
        </button>
      </nav>

      <br></br>
      <div className="col-md-6">
        <h4>Employees List</h4>

        <div className="container">
          <div className="row">
            <div className="col-12">
              <table className="table table-bordered table-striped">
                <thead>
                  <tr>
                    <th>Id</th>
                    <th>Name</th>
                    <th>Address</th>
                    <th>Mobile Number</th>
                    <th scope="col">Action</th>
                  </tr>
                </thead>
                <tbody>
                  {employees &&
                    employees.map((employee, index) => (
                      <tr>
